import{_ as a,o as e,c as i,Q as r}from"./chunks/framework.273ff57c.js";const x=JSON.parse('{"title":"margin、padding 取值问题","description":"","frontmatter":{},"headers":[],"relativePath":"interview/css/margin-padding.md","filePath":"interview/css/margin-padding.md","lastUpdated":1699244082000}'),n={name:"interview/css/margin-padding.md"},d=r('<h1 id="margin、padding-取值问题" tabindex="-1">margin、padding 取值问题 <a class="header-anchor" href="#margin、padding-取值问题" aria-label="Permalink to &quot;margin、padding 取值问题&quot;">​</a></h1><p>margin 这个 CSS 属性可以同时配置四个 css 属性<code>margin-left</code>、<code>margin-right</code>、<code>,margin-bottom</code>、<code>margin-top</code>。</p><p>但是属性值可以是四个、三个、两个、两个取值，取值个数不同其设置的属性也是不同的。</p><h2 id="margin" tabindex="-1">margin <a class="header-anchor" href="#margin" aria-label="Permalink to &quot;margin&quot;">​</a></h2><h3 id="四个取值" tabindex="-1">四个取值 <a class="header-anchor" href="#四个取值" aria-label="Permalink to &quot;四个取值&quot;">​</a></h3><p>margin：1px 2px 3px 4px 则结果为：上 1px，右边 2px，下 3px，左 4px</p><h3 id="三个取值" tabindex="-1">三个取值 <a class="header-anchor" href="#三个取值" aria-label="Permalink to &quot;三个取值&quot;">​</a></h3><p>​margin：1px 2px 4px 则结果为：上 1px、下 4px，左右 2px。</p><h3 id="两个取值" tabindex="-1">两个取值 <a class="header-anchor" href="#两个取值" aria-label="Permalink to &quot;两个取值&quot;">​</a></h3><p>margin：10px 15px，则结果为：上下 10px，左右 15px</p><h3 id="一个取值" tabindex="-1">一个取值 <a class="header-anchor" href="#一个取值" aria-label="Permalink to &quot;一个取值&quot;">​</a></h3><p>margin：10px，则结果为：上下左右 10px</p><h2 id="padding" tabindex="-1">padding <a class="header-anchor" href="#padding" aria-label="Permalink to &quot;padding&quot;">​</a></h2><p>padding 同理。</p>',14),t=[d];function o(p,c,s,h,l,m){return e(),i("div",null,t)}const _=a(n,[["render",o]]);export{x as __pageData,_ as default};
